import api from '@/services/api';
import axios from 'axios';
export default {
  name: 'ProfileViewSimple',
  data() {
    return {
      userProfile: {
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        address: '',
        status: ''
      },
      profilePictureUrl: null,
      isLoading: false,
      errors: {
        firstName: '',
        lastName: ''
      },
      alert: {
        show: false,
        message: '',
        type: 'success'
      }
    };
  },
  computed: {
    userInitial() {
      return this.userProfile.email ? this.userProfile.email.charAt(0).toUpperCase() : '?';
    }
  },
  methods: {
    async fetchUserProfile() {
      try {
        this.isLoading = true;
        const response = await api.getUserProfile();
        const userData = response.data.user;
        this.userProfile = {
          firstName: userData.first_name || '',
          lastName: userData.last_name || '',
          email: userData.user_email || '',
          phone: userData.phone || '',
          address: userData.address || '',
          status: userData.status || ''
        };

        // First try to get from localStorage (base64 data) as it's most reliable
        const storedPictureUrl = localStorage.getItem('profilePictureUrl');
        if (storedPictureUrl && storedPictureUrl.startsWith('data:image/')) {
          this.profilePictureUrl = storedPictureUrl;
          return;
        }

        // If no localStorage image, check if profile has an image
        if (userData.profile_picture) {
          // For external URLs (like randomuser.me), use directly
          if (userData.profile_picture.startsWith('http')) {
            this.profilePictureUrl = userData.profile_picture;
          } else {
            // For server URLs, add timestamp and full path
            const timestamp = new Date().getTime();
            const serverUrl = `http://localhost:5000${userData.profile_picture}?t=${timestamp}`;
            this.profilePictureUrl = serverUrl;
          }
        }
      } catch (error) {
        console.error('Error fetching user profile:', error);
        this.showAlert('ไม่สามารถโหลดข้อมูลโปรไฟล์ได้ กรุณาลองใหม่อีกครั้ง', 'error');
      } finally {
        this.isLoading = false;
      }
    },
    validateForm() {
      let isValid = true;
      this.errors.firstName = '';
      this.errors.lastName = '';
      if (!this.userProfile.firstName.trim()) {
        this.errors.firstName = 'กรุณากรอกชื่อ';
        isValid = false;
      }
      if (!this.userProfile.lastName.trim()) {
        this.errors.lastName = 'กรุณากรอกนามสกุล';
        isValid = false;
      }
      return isValid;
    },
    async saveProfile() {
      if (!this.validateForm()) {
        return;
      }
      try {
        this.isLoading = true;
        const response = await api.updateUserProfile({
          firstName: this.userProfile.firstName,
          lastName: this.userProfile.lastName,
          phone: this.userProfile.phone,
          address: this.userProfile.address
        });
        console.log('Profile updated:', response.data);
        this.showAlert('อัปเดตโปรไฟล์เรียบร้อยแล้ว', 'success');
      } catch (error) {
        console.error('Error updating profile:', error);
        this.showAlert('ไม่สามารถอัปเดตโปรไฟล์ได้ กรุณาลองใหม่อีกครั้ง', 'error');
      } finally {
        this.isLoading = false;
      }
    },
    triggerPhotoUpload() {
      // Trigger the file input click
      this.$refs.photoInput.click();
    },
    async handleFileSelected(event) {
      const file = event.target.files[0];
      if (!file) return;

      // Validate file is an image - ตรวจสอบว่าเป็นไฟล์รูปภาพ
      if (!file.type.startsWith('image/')) {
        this.showAlert('กรุณาอัปโหลดไฟล์รูปภาพเท่านั้น', 'error');
        return;
      }

      // ตรวจสอบขนาดไฟล์ - เพิ่มขนาดสูงสุดเป็น 10MB
      const maxSizeInBytes = 10 * 1024 * 1024; // 10MB
      if (file.size > maxSizeInBytes) {
        this.showAlert(`ไฟล์มีขนาดใหญ่เกินไป (${(file.size / (1024 * 1024)).toFixed(2)} MB) กรุณาอัปโหลดไฟล์ขนาดไม่เกิน 10MB`, 'error');
        return;
      }

      // Show file info for debugging
      console.log('Selected file:', file.name, file.type, file.size);

      // Show loading alert
      this.showAlert('กำลังอัปโหลดรูปโปรไฟล์...', 'info');

      // Convert file to base64
      const reader = new FileReader();
      reader.onload = async e => {
        try {
          let imageData = e.target.result;

          // Log the image data format for debugging
          console.log('Image data format:', imageData.substring(0, 50) + '...');

          // Ensure correct MIME type for all image files
          // ตรวจสอบและแก้ไข MIME type ให้ตรงกับประเภทไฟล์
          if (file.type === 'image/jpeg' || file.type === 'image/jpg') {
            // Make sure the MIME type in the data URL matches the file type
            if (!imageData.startsWith('data:image/jpeg')) {
              imageData = imageData.replace(/^data:image\/[^;]+/, 'data:image/jpeg');
            }
          } else if (file.type === 'image/png' && !imageData.startsWith('data:image/png')) {
            imageData = imageData.replace(/^data:image\/[^;]+/, 'data:image/png');
          } else if (file.type === 'image/gif' && !imageData.startsWith('data:image/gif')) {
            imageData = imageData.replace(/^data:image\/[^;]+/, 'data:image/gif');
          } else if (file.type === 'image/webp' && !imageData.startsWith('data:image/webp')) {
            imageData = imageData.replace(/^data:image\/[^;]+/, 'data:image/webp');
          } else if (file.type === 'image/svg+xml' && !imageData.startsWith('data:image/svg+xml')) {
            imageData = imageData.replace(/^data:image\/[^;]+/, 'data:image/svg+xml');
          } else if (file.type === 'image/bmp' && !imageData.startsWith('data:image/bmp')) {
            imageData = imageData.replace(/^data:image\/[^;]+/, 'data:image/bmp');
          }

          // ตรวจสอบว่า imageData มีรูปแบบที่ถูกต้อง
          if (!imageData.includes('base64,')) {
            console.error('Invalid image data format:', imageData.substring(0, 50) + '...');
            this.showAlert('รูปแบบข้อมูลรูปภาพไม่ถูกต้อง กรุณาลองใหม่อีกครั้ง', 'error');
            return;
          }

          // Show preview immediately
          this.profilePictureUrl = imageData;

          // Get user ID from localStorage
          const userId = localStorage.getItem('userId');

          // Upload the image data
          console.log('Uploading to userId:', userId);

          // ตรวจสอบว่ามี token หรือไม่
          const token = localStorage.getItem('token');
          if (!token) {
            console.error('No authentication token found');
            this.showAlert('ไม่พบข้อมูลการเข้าสู่ระบบ กรุณาเข้าสู่ระบบใหม่อีกครั้ง', 'error');
            return;
          }

          // ใช้ api service ที่เราสร้างไว้
          console.log('Using api service to upload profile picture');
          try {
            // ใช้ api.uploadProfilePicture ที่เราเพิ่มไว้ใน api.js
            const response = await api.uploadProfilePicture(userId, imageData);

            // ถ้าใช้ api service จะได้ข้อมูลใน response.data โดยตรง
            console.log('Profile picture uploaded:', response.data);
            if (response.data.profilePictureUrl) {
              // Keep using the base64 image data for display
              localStorage.setItem('profilePictureUrl', imageData);

              // Store the server URL for future page loads
              localStorage.setItem('serverProfilePictureUrl', response.data.profilePictureUrl);
            }
            this.showAlert('อัปโหลดรูปโปรไฟล์เรียบร้อยแล้ว', 'success');
            return; // ออกจากฟังก์ชันเพราะสำเร็จแล้ว
          } catch (error) {
            // จัดการข้อผิดพลาด
            console.error('API error:', error);
            let errorMessage = 'ไม่สามารถอัปโหลดรูปโปรไฟล์ได้';
            if (error.response) {
              // มีการตอบกลับจากเซิร์ฟเวอร์
              console.error('Response error data:', error.response.data);
              console.error('Response status:', error.response.status);
              if (error.response.data && error.response.data.message) {
                errorMessage = `${errorMessage}: ${error.response.data.message}`;
              } else if (error.response.status === 413) {
                errorMessage = 'ไฟล์รูปภาพมีขนาดใหญ่เกินไป กรุณาลดขนาดไฟล์และลองใหม่อีกครั้ง';
              } else if (error.response.status === 415) {
                errorMessage = 'รูปแบบไฟล์ไม่รองรับ กรุณาใช้ไฟล์รูปภาพที่มีนามสกุล .jpg, .png, .gif หรืออื่นๆ';
              } else if (error.response.status === 404) {
                errorMessage = 'ไม่พบ API สำหรับอัปโหลดรูปภาพ กรุณาติดต่อผู้ดูแลระบบ';
              } else if (error.response.status === 500) {
                errorMessage = 'เซิร์ฟเวอร์เกิดข้อผิดพลาด กรุณาลองใหม่อีกครั้งหรือติดต่อผู้ดูแลระบบ';
              }
            } else if (error.request) {
              // ไม่ได้รับการตอบกลับจากเซิร์ฟเวอร์
              errorMessage = 'ไม่สามารถเชื่อมต่อกับเซิร์ฟเวอร์ได้ กรุณาตรวจสอบการเชื่อมต่ออินเทอร์เน็ต';
            } else {
              // ข้อผิดพลาดอื่นๆ
              errorMessage = `${errorMessage}: ${error.message}`;
            }
            this.showAlert(errorMessage, 'error');
            return; // ออกจากฟังก์ชันเพราะเกิดข้อผิดพลาด
          }
          if (response.ok) {
            const data = await response.json();
            console.log('Profile picture uploaded:', data);
            if (data.profilePictureUrl) {
              // Keep using the base64 image data for display
              // This ensures the image stays visible
              localStorage.setItem('profilePictureUrl', imageData);

              // Store the server URL for future page loads
              localStorage.setItem('serverProfilePictureUrl', data.profilePictureUrl);
            }
            this.showAlert('อัปโหลดรูปโปรไฟล์เรียบร้อยแล้ว', 'success');

            // Don't dispatch event or reload the page
            // This prevents flickering and unnecessary reloads
          } else {
            console.error('Failed to upload profile picture. Status:', response.status, response.statusText);

            // ข้อความแสดงข้อผิดพลาดที่เฉพาะเจาะจงมากขึ้น
            let errorMessage = 'ไม่สามารถอัปโหลดรูปโปรไฟล์ได้';
            try {
              // ตรวจสอบว่าการตอบกลับเป็น JSON หรือไม่
              const contentType = response.headers.get('content-type');
              if (contentType && contentType.includes('application/json')) {
                const errorData = await response.json();
                console.error('Error data:', errorData);
                if (errorData.message) {
                  if (errorData.message.includes('Invalid image format')) {
                    errorMessage = 'รูปแบบไฟล์ไม่ถูกต้อง กรุณาใช้ไฟล์รูปภาพที่มีนามสกุล .jpg, .png, .gif หรืออื่นๆ';
                  } else {
                    errorMessage = `${errorMessage}: ${errorData.message}`;
                  }
                }
              } else {
                // ถ้าไม่ใช่ JSON ให้ใช้ status code แทน
                const responseText = await response.text();
                console.error('Response is not JSON:', responseText.substring(0, 100) + '...');
              }
            } catch (parseError) {
              console.error('Error parsing response:', parseError);
            }

            // ใช้ status code ในกรณีที่ไม่สามารถอ่าน JSON ได้
            if (response.status === 413) {
              errorMessage = 'ไฟล์รูปภาพมีขนาดใหญ่เกินไป กรุณาลดขนาดไฟล์และลองใหม่อีกครั้ง';
            } else if (response.status === 415) {
              errorMessage = 'รูปแบบไฟล์ไม่รองรับ กรุณาใช้ไฟล์รูปภาพที่มีนามสกุล .jpg, .png, .gif หรืออื่นๆ';
            } else if (response.status === 404) {
              errorMessage = 'ไม่พบ API สำหรับอัปโหลดรูปภาพ กรุณาติดต่อผู้ดูแลระบบ';
            } else if (response.status === 500) {
              errorMessage = 'เซิร์ฟเวอร์เกิดข้อผิดพลาด กรุณาลองใหม่อีกครั้งหรือติดต่อผู้ดูแลระบบ';
            } else {
              errorMessage = `${errorMessage}: ${response.status} ${response.statusText}`;
            }
            this.showAlert(errorMessage, 'error');

            // Reset the file input so the user can try again
            this.$refs.photoInput.value = '';
          }
        } catch (error) {
          console.error('Error uploading profile picture:', error);

          // ข้อความแสดงข้อผิดพลาดที่เฉพาะเจาะจงมากขึ้น
          let errorMessage = 'ไม่สามารถอัปโหลดรูปโปรไฟล์ได้';
          if (error.message) {
            if (error.message.includes('NetworkError') || error.message.includes('Failed to fetch')) {
              errorMessage = 'ไม่สามารถเชื่อมต่อกับเซิร์ฟเวอร์ได้ กรุณาตรวจสอบการเชื่อมต่ออินเทอร์เน็ต';
            } else if (error.message.includes('timeout')) {
              errorMessage = 'การเชื่อมต่อกับเซิร์ฟเวอร์ใช้เวลานานเกินไป กรุณาลองใหม่อีกครั้ง';
            } else {
              errorMessage = `${errorMessage}: ${error.message}`;
            }
          } else {
            errorMessage = `${errorMessage}: เกิดข้อผิดพลาดที่ไม่ทราบสาเหตุ`;
          }
          this.showAlert(errorMessage, 'error');

          // Reset the file input so the user can try again
          this.$refs.photoInput.value = '';
        }
      };
      reader.readAsDataURL(file);
    },
    formatStatus(status) {
      if (!status) return 'ไม่ทราบสถานะ';
      const statusMap = {
        'headman': 'ผู้ใหญ่บ้าน',
        'assistant': 'ผู้ช่วยผู้ใหญ่บ้าน',
        'villager': 'ลูกบ้าน',
        'pending': 'รอการอนุมัติ'
      };
      return statusMap[status.toLowerCase()] || status.charAt(0).toUpperCase() + status.slice(1);
    },
    showAlert(message, type = 'success') {
      this.alert = {
        show: true,
        message,
        type
      };

      // Hide alert after 3 seconds
      setTimeout(() => {
        this.alert.show = false;
      }, 3000);
    },
    getFullImageUrl(url) {
      if (!url) return '';

      // If it's a data URL (base64), return as is
      if (url.startsWith('data:')) {
        return url;
      }

      // If it's a relative URL, prepend the API base URL
      if (url.startsWith('/')) {
        // Add a timestamp to prevent caching
        const timestamp = new Date().getTime();
        // Always use the full URL with localhost
        return `http://localhost:5000${url}?t=${timestamp}`;
      }

      // If it's an https URL (like randomuser.me), return as is
      if (url.startsWith('http')) {
        return url;
      }

      // Otherwise, return the URL as is
      return url;
    },
    handleImageError(e) {
      console.error('Error loading image:', e);
      console.log('Failed image URL:', e.target.src);

      // Try to load the image directly from the server
      const url = e.target.src;
      if (url.includes('/uploads/')) {
        const imagePath = url.split('?')[0]; // Remove query parameters
        const newUrl = `http://localhost:5000${imagePath.substring(imagePath.indexOf('/uploads/'))}`;
        console.log('Trying alternative URL:', newUrl);
        e.target.src = newUrl;
        return;
      }

      // Try to use stored base64 image if available
      const storedPictureUrl = localStorage.getItem('profilePictureUrl');
      if (storedPictureUrl && storedPictureUrl.startsWith('data:image/')) {
        console.log('Using stored base64 image');
        this.profilePictureUrl = storedPictureUrl;
        return;
      }

      // If all else fails, fall back to placeholder
      this.profilePictureUrl = null;
    }
  },
  mounted() {
    this.fetchUserProfile();
  }
};
