import axios from 'axios';

// Use the axios instance from main.js
const api = {
  // Auth endpoints
  login(credentials) {
    return axios.post('/auth/login', credentials);
  },
  register(userData) {
    return axios.post('/auth/register', userData);
  }
};

export default api;
